command:example.dev:code:
PS C:\> {{ $SD = Get-SMBSecurity -SecurityDescriptorName SrvsvcDefaultShareInfo }}
PS C:\> {{ $DACLSplat = @{
               SecurityDescriptorName = 'SrvsvcDefaultShareInfo'
               Access                 = 'Allow'
               Right                  = 'FullControl'
               Account                = 'Authenticated Users'
           } }}
PS C:\> {{ $DACL = New-SMBSecurityDACL @DACLSplat }}
PS C:\> {{ Add-SMBSecurityDACL -SecurityDescriptor $SD -DACL $DACL }}

PS C:\> {{ $SMBSec = Get-SMBSecurity -SecurityDescriptorName SrvsvcDefaultShareInfo }}
PS C:\> {{ $DACL = $SMBSec.DACL | Where-Object { $_.Account.Username -eq "Authenticated Users" } }}
PS C:\> {{ $NewDACL = Copy-SMBSecurityDACL $DACL }}
PS C:\> {{ Set-SMBSecurityDACL -DACL $NewDACL -Right Read }}
PS C:\> {{ Set-SmbSecurityDescriptorDACL -SecurityDescriptor $SMBSec -DACL $DACL -NewDACl $NewDACL }}

PS C:\> {{ $DACLSplat = @{
                SecurityDescriptor = 'SrvsvcDefaultShareInfo'
                Access             = 'Allow'
                Right              = 'FullControl'
                Account            = "Administrators"
            } }}
PS C:\> {{ $DACL = New-SMBSecurityDACL @DACLSplat }}

PS C:\> {{ $DACLs = @() }}
PS C:\> {{ $DACLSplat = @{ }}
PS C:\> {{     SecurityDescriptor = 'SrvsvcDefaultShareInfo' }}
PS C:\> {{     Access             = 'Allow' }}
PS C:\> {{     Right              = 'FullControl' }}
PS C:\> {{     Account            = "Administrators" }}
PS C:\> {{ } }}
PS C:\> {{ $DACL = New-SMBSecurityDACL @DACLSplat }}
PS C:\> {{ $DACLSplat2 = @{ }}
PS C:\> {{     SecurityDescriptor = 'SrvsvcDefaultShareInfo' }}
PS C:\> {{     Access             = 'Allow' }}
PS C:\> {{     Right              = 'Read' }}
PS C:\> {{     Account            = "Authenticated Users" }}
PS C:\> {{ } }}
PS C:\> {{ $DACL2 = New-SMBSecurityDACL @DACLSplat2 }}
PS C:\> {{ $DACLs += $DACL }}
PS C:\> {{ $DACLs += $DACL2 }}
PS C:\> {{ $account = "NT AUTHORITY\SYSTEM" }}
PS C:\> {{ $Owner = New-SMBSecurityOwner -Account $account }}
PS C:\> {{ $Group = New-SMBSecurityGroup -Account $account }}
PS C:\> {{ $SD = New-SMBSecurityDescriptor -SecurityDescriptor "SrvsvcDefaultShareInfo" -Owner $Owner -Group $GroupPS -DACL $DACLs }}

PS C:\> {{ $SD = Get-SMBSecurity -SecurityDescriptorName SrvsvcSharePrintInfo }}
PS C:\> {{ $DACL = $SD | Where-Object {$_.Account.Username -eq "Everyone"} }}
PS C:\> {{ $DACL | Remove-SMBSecurityDACL -SecurityDescriptor $SD }}

PS C:\> {{ $SD = Get-SMBSecurity -SecurityDescriptorName SrvsvcSharePrintInfo }}
PS C:\> {{ $DACL = $SD | Where-Object {$_.Account.Username -eq "Everyone"} }}
PS C:\> {{ $DACL | Remove-SMBSecurityDACL -SecurityDescriptor $SD }}

PS C:\> {{ $SD = Get-SMBSecurity -SecurityDescriptorName SrvsvcDefaultShareInfo }}
PS C:\> {{ $DACL = $SMBSec.DACL | Where-Object {$_.Account.Username -eq "Everyone"} }}
PS C:\> {{ $DACL | Remove-SMBSecurityDACL -SecurityDescriptor $SMBSec }}
PS C:\> {{ Save-SMBSecurity -SecurityDescriptor $SMBSec }}

PS C:\> {{ $SD = Get-SMBSecurity -SecurityDescriptorName SrvsvcSharePrintInfo }}
PS C:\> {{ $DACL = $SD.DACL | Where-Object {$_.Account.Username -eq "DomianGroup"} }}
PS C:\> {{ $NewDACL = Copy-SMBSecurityDACL $DACL }}
PS C:\> {{ Set-SMBSecurityDACL -DACL $NewDACL -Access Deny }}

